INSERT INTO `dpd_email_content` (`email_content_id`, `email_name`, `email_alias`, `email_subject`, `email_content`, `marketing_email`) VALUES
(1, 'Customer Registration', 'customer_registration', 'DesktopDeli.com - Account Awaiting Approval', '<div style="font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px;">\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td style="color: #000000; vertical-align: top; width: 50%;"><strong>Account Awaiting Approval&nbsp;</strong><br /> Date: {DATE}</td>\n</tr>\n</tbody>\n</table>\n<br />\n<p>&nbsp;</p>\n<p>Dear Customer,</p>\n<p>Your customer account at <a href="{COMPANY_URL}" target="_blank">{COMPANY_NAME}</a>&nbsp;has been created. Please click on the link given below for confirmation to your account.&nbsp;</p>\n<p>Username: {USERNAME}&nbsp;<br />Password: {PASSWORD}</p>\n<p><a href="{LINK}">CLICK HERE</a>&nbsp;to approve your account</p>\n<p>&nbsp;</p>\n<p>Thanks,&nbsp;<br />DesktopDeli</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</div>', 0),
(2, 'Account Activation', 'account_activation', 'Your desktopdeli account is approved !', '<div style="font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px;">\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td style="color: #000000; vertical-align: top; width: 50%;"><strong> Account Approved </strong> <br /> Date: {DATE}</td>\n</tr>\n</tbody>\n</table>\n<p>Dear&nbsp;{NAME}&nbsp;,</p>\n<p>Welcome to {COMPANY_NAME}&nbsp;desktopdeli ordering</p>\n<p><span>Your account at {COMPANY_URL} has been approved&nbsp;</span>.You may now login and place orders&nbsp;and experience the most convenience lunch service ever.</p>\n<p>Username: {USERNAME}&nbsp;<br />Password: {PASSWORD}</p>\n<p><a href="{LINK}">Click here</a>&nbsp;to login</p>\n<p>Thanks,&nbsp;<br />DesktopDeli</p>\n<p>&nbsp;</p>\n</div>', 0),
(3, 'Order Placed Pay As You Go', 'pay_as_you_go_order', 'DesktopDeli.com - Order Received', '<div style="font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px;">\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td style="color: #000000; vertical-align: top; width: 50%;">Order Received Successfully <br /> Dated: {DATE}</td>\n</tr>\n</tbody>\n</table>\n<br />\n<p>Dear {NAME},</p>\n<p>Thank you for placing your order at DesktopDeli. See below for details:</p>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="0">\n<tbody>\n<tr>\n<td width="20%">Order No.</td>\n<td width="80%">: {ORDER_NO}</td>\n</tr>\n<tr>\n<td width="20%">Location</td>\n<td width="80%"><span>:{LOCATION_NAME}</span></td>\n</tr>\n<tr>\n<td width="20%">Delivery Date</td>\n<td width="80%"><span>:{DELIVERY_DATE}</span></td>\n</tr>\n<tr>\n<td width="20%">Delivery Time</td>\n<td width="80%"><span>:{DELIVERY_TIME}</span></td>\n</tr>\n<tr>\n<td>Subtotal</td>\n<td>: {SUB_TOTAL}</td>\n</tr>\n<tr>\n<td>Total</td>\n<td>: {TOTAL}</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td valign="top"><strong> Delivery Address </strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td width="20%" valign="top">Name</td>\n<td width="80%">{NAME}</td>\n</tr>\n<tr>\n<td>Address 1:</td>\n<td>{DELIVERY_ADDRESS_1}</td>\n</tr>\n<tr>\n<td>Address 2 :</td>\n<td>{DELIVERY_ADDRESS_2}</td>\n</tr>\n<tr>\n<td>Town/City :</td>\n<td>{DELIVERY_CITY}</td>\n</tr>\n<tr>\n<td>State :</td>\n<td>{DELIVERY_STATE}</td>\n</tr>\n<tr>\n<td>Post Code :</td>\n<td>{DELIVERY_ZIPCODE}</td>\n</tr>\n</tbody>\n</table>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td valign="top"><strong> Billing Address </strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td width="20%" valign="top">&nbsp;</td>\n<td width="80%">&nbsp;</td>\n</tr>\n<tr>\n<td>Address 1:</td>\n<td>{BILLING_ADDRESS_1}</td>\n</tr>\n<tr>\n<td>Address 2 :</td>\n<td>{BILLING_ADDRESS_2}</td>\n</tr>\n<tr>\n<td>Town/City :</td>\n<td>{BILLING_CITY}</td>\n</tr>\n<tr>\n<td>State :</td>\n<td>{BILLING_STATE}</td>\n</tr>\n<tr>\n<td>Post Code :</td>\n<td>{BILLING_ZIPCODE}</td>\n</tr>\n<tr>\n<td>Phone :</td>\n<td>{BILLING_PHONE}</td>\n</tr>\n</tbody>\n</table>\n{ORDER_DETAILS}</div>', 0),
(4, 'Order Placed Credit Limit', 'credit_card_order', 'DesktopDeli.com - Order Received', '<div style="font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px;">\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td style="color: #000000; vertical-align: top; width: 50%;">Order Received Successfully <br /> Dated: {DATE}</td>\n</tr>\n</tbody>\n</table>\n<br />\n<p>Dear {NAME},</p>\n<p>Thank you for placing your order at DesktopDeli. See below for details:</p>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="0">\n<tbody>\n<tr>\n<td width="20%">Order No.</td>\n<td width="80%">: {ORDER_NO}</td>\n</tr>\n<tr>\n<td width="20%">Location</td>\n<td width="80%">:{LOCATION_NAME}</td>\n</tr>\n<tr>\n<td width="20%">Delivery Date</td>\n<td width="80%">:{DELIVERY_DATE}</td>\n</tr>\n<tr>\n<td width="20%"><span>Delivery Time</span></td>\n<td width="80%">:{DELIVERY_TIME}</td>\n</tr>\n<tr>\n<td>Subtotal</td>\n<td>: {SUB_TOTAL}</td>\n</tr>\n<tr>\n<td>Total</td>\n<td>: {TOTAL}</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n{ORDER_DETAILS}</div>', 0),
(5, 'Cafe2u', 'marketing-email', 'Cafe2u - online deli', '<div style="font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px;">\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n<tbody>\n<tr>\n<td style="color: #000000; vertical-align: top; width: 50%;">Marketing <br /> Dated: {DATE}</td>\n</tr>\n</tbody>\n</table>\n<br />\n<p>Dear, {FIRST_NAME} <span> {LAST_NAME} </span> &nbsp;,</p>\n<p>I would like to welcome you to a revolution in the world of cafe2u. You can now click the link below and it will take you to our huge online shop where you will be able to order your food, breakfast, snacks and drinks from your desktop, smartphone or tablet.</p>\n<p>Click the link below and get 20% off your first order&nbsp;</p>\n<p>check out our facebook page for offers and rewards</p>\n<p><span> {COMPANY} </span> <span> &nbsp;,&nbsp;</span>&nbsp;<br /> Cafe2u</p>\n<p><a href="{PREVIEW_URL}" target="_blank">Click here to open this newsletter in your browser.</a></p>\n<p><a href="{UNSUBSCRIBE_URL}" target="_blank">CLick here to unsubscribe.</a></p>\n<table style="width: 216px; height: 38px;" border="0">\n<tbody>\n<tr>\n<td>test1</td>\n<td>test2</td>\n</tr>\n<tr>\n<td>test3</td>\n<td>test4</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</div>', 1);
